import React from "react";
import "./App.css";
const App = () => {
  const [todos, setTodos] = React.useState([]);
  const [todo, setTodo] = React.useState("");

  // Add the handlesubmit code here
  function handleSubmit(e) {
    e.preventDefault()

    const newTodo = {
      id: new Date().getTime(),
      text: todo.trim(),
      completed: false,
    };
    if (newTodo.text.length > 0) {

      setTodos([...todos].concat(newTodo));
      console.log(todos, "added")
      setTodo("");
    } else {
      alert("Enter Valid Task");
      setTodo("");
    }
  }

  // Add the deleteToDo code here
  // function deleteTodo(e) {
  //   e.preventDefault();


  //   let num = undefined
  //   const newTodos = todos.filter((obj, index) => {
  //     if (obj.text.toLowerCase() === todo.toLowerCase()) {
  //       num = index
  //     }
  //     return obj.text.toLowerCase() === todo.toLowerCase()
  //   })

  //   console.log(newTodos, "this is todos.")

  //   if (newTodos.length === 0) {

  //     alert("Please insert a valid todo item.")
  //   } else {

  //     let todosTemp = []
  //     todos.map((todo, index) => {
  //       if (index !== num) {
  //         todosTemp.push(todo)
  //       }

  //     })
  //     setTodos(todosTemp)
  //   }

  // }
  function deleteTodo(id) {
    let updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  }

  // Add the toggleComplete code here
  function toggleComplete(id) {
    const todosTemp = todos.filter((ob) => ob.id === id)
    console.log("Before setting the completion.")
    setTodos(!todosTemp[0].completed)
    console.log("After the completion.")
    // let updatedTodos = [...todos].map((todo) => {
    //   if (todo.id === id) {
    //     todo.completed = !todo.completed;
    //   }
    //   return todo;
    // });
    // setTodos(updatedTodos);
  }

  // Add the submitEdits code here


  return (
    <div className="App">
      <h1>Todo List</h1>
      <form onSubmit={handleSubmit}>
        <input placeholder="Add a new task" type="text" onChange={(e) => setTodo(e.target.value)} align="right" />
        <button type="submit" >Add Todo</button>
        {/* <button type="submit" onClick={deleteTodo}>Delete Todo</button> */}
      </form>
      {todos.map((todo) => <div className="todo" key={todo.id}> <div>{todo.text}</div>
        <button onClick={() => deleteTodo(todo.id)}>Delete</button>

        <input type="checkbox" id="completed" checked={todo.completed} onChange={() => toggleComplete(todo.id)} />
      </div>)}


    </div>
  );
};
export default App;
